# %%

cave_width = 7


def pad_line(line):
    return ("." * 2) + line.replace("#", "@") + ("." * (cave_width - len(line) - 2))


rocks = list(
    map(
        lambda x: tuple(map(pad_line, x.splitlines())),
        """
####

.#.
###
.#.

..#
..#
###

#
#
#
#

##
##
""".strip().split(
            "\n\n"
        ),
    )
)

